package com.example.hw_4;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.RecyclerView;

import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import java.util.ArrayList;


public class ContinentsFragment extends Fragment implements OnItemClick {

ArrayList<Continent>continentArrayList = new ArrayList<>();
private  RecyclerView rvContinent;
    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        // Inflate the layout for this fragment
        return inflater.inflate(R.layout.fragment_continents, container, false);
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        rvContinent=requireActivity().findViewById(R.id.rv_continent);

        loadData();
        ContinentAdapter adapter  = new ContinentAdapter(continentArrayList,this);
        rvContinent.setAdapter(adapter);
    }

    private void loadData() {
        continentArrayList.add(new Continent("Евразия","data:image/jpeg;base64,"));
        continentArrayList.add(new Continent("СевернаяАмерика","https://upload.wikimedia.org/wikipedia/commons/thumb/9/99/North_America_satellite_globe.jpg/220px-North_America_satellite_globe.jpg"));
        continentArrayList.add(new Continent("ЮжнаяАмерика","data:image/jpeg;base64,"));
        continentArrayList.add(new Continent("Африка","data:image/jpeg;base64,"));
        continentArrayList.add(new Continent("Австралия","data:image/jpeg;base64,"));
    }


    @Override
    public void onClick(Integer position) {
        Bundle bundle = new Bundle();
        bundle.putInt("Key",position);
        CountryFragment countryFragment = new CountryFragment();
        countryFragment.setArguments(bundle);

        requireActivity().getSupportFragmentManager().beginTransaction().replace(R.id.container,countryFragment).addToBackStack(null).commit();
    }
}